package com.example.appajicolorgrupo4.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.appajicolorgrupo4.data.local.database.AppDatabase
import com.example.appajicolorgrupo4.data.repository.UserRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch

data class LoginUiState(
    val correo: String = "",
    val clave: String = "",
    val correoError: String? = null,
    val claveError: String? = null,
    val isLoading: Boolean = false,
    val loginError: String? = null,
    val loginExitoso: Boolean = false
)

class LoginViewModel(application: Application) : AndroidViewModel(application) {

    // Repositorio
    private val repository: UserRepository

    init {
        val database = AppDatabase.getInstance(application)
        repository = UserRepository(database.userDao())
    }

    // Estado del login
    private val _estado = MutableStateFlow(LoginUiState())
    val estado: StateFlow<LoginUiState> = _estado

    // Actualizar correo
    fun actualizaCorreo(valor: String) {
        _estado.update {
            it.copy(
                correo = valor,
                correoError = null,
                loginError = null
            )
        }
    }

    // Actualizar clave
    fun actualizaClave(valor: String) {
        _estado.update {
            it.copy(
                clave = valor,
                claveError = null,
                loginError = null
            )
        }
    }

    // Validar campos
    private fun validarCampos(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var correoError: String? = null
        var claveError: String? = null

        if (estadoActual.correo.isBlank()) {
            correoError = "El correo es obligatorio"
            valido = false
        } else if (!estadoActual.correo.contains("@")) {
            correoError = "Correo inválido"
            valido = false
        }

        if (estadoActual.clave.isBlank()) {
            claveError = "La contraseña es obligatoria"
            valido = false
        }

        _estado.update {
            it.copy(
                correoError = correoError,
                claveError = claveError
            )
        }

        return valido
    }

    // Iniciar sesión
    fun iniciarSesion(onSuccess: () -> Unit) {
        if (!validarCampos()) {
            return
        }

        val estadoActual = _estado.value
        _estado.update { it.copy(isLoading = true, loginError = null) }

        viewModelScope.launch {
            val resultado = repository.login(
                correo = estadoActual.correo,
                clave = estadoActual.clave
            )

            resultado.onSuccess { user ->
                _estado.update {
                    it.copy(
                        isLoading = false,
                        loginExitoso = true,
                        loginError = null
                    )
                }
                onSuccess()
            }.onFailure { error ->
                _estado.update {
                    it.copy(
                        isLoading = false,
                        loginExitoso = false,
                        loginError = error.message ?: "Error al iniciar sesión"
                    )
                }
            }
        }
    }

    // Limpiar errores
    fun limpiarErrores() {
        _estado.update {
            it.copy(
                correoError = null,
                claveError = null,
                loginError = null
            )
        }
    }
}

package com.example.appajicolorgrupo4.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.appajicolorgrupo4.data.local.database.AppDatabase
import com.example.appajicolorgrupo4.data.repository.UserRepository
import com.example.appajicolorgrupo4.data.session.SessionManager
import com.example.appajicolorgrupo4.data.local.user.UserEntity
import com.example.appajicolorgrupo4.ui.state.UsuarioUiState
import com.example.appajicolorgrupo4.ui.state.UsuarioErrores
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch

class UsuarioViewModel(application: Application) : AndroidViewModel(application) {

    // Repositorio y SessionManager
    private val repository: UserRepository
    private val sessionManager: SessionManager

    init {
        val database = AppDatabase.getInstance(application)
        repository = UserRepository(database.userDao())
        sessionManager = SessionManager(application)
    }

    // Estado privado mutable
    private val _estado = MutableStateFlow(UsuarioUiState())
    // Estado público inmutable para la UI
    val estado: StateFlow<UsuarioUiState> = _estado

    // Estado para mensajes de éxito/error al registrar
    private val _registroResultado = MutableStateFlow<String?>(null)
    val registroResultado: StateFlow<String?> = _registroResultado

    // Estado para el usuario actual
    private val _currentUser = MutableStateFlow<UserEntity?>(null)
    val currentUser: StateFlow<UserEntity?> = _currentUser

    // Estado para el modo de edición
    private val _isEditMode = MutableStateFlow(false)
    val isEditMode: StateFlow<Boolean> = _isEditMode

    // Estado para mensajes de actualización
    private val _updateResultado = MutableStateFlow<String?>(null)
    val updateResultado: StateFlow<String?> = _updateResultado

    // Estado para la URI de la foto de perfil
    private val _profileImageUri = MutableStateFlow<String?>(null)
    val profileImageUri: StateFlow<String?> = _profileImageUri

    // Funciones de actualización de campos
    fun actualizaNombre(valor: String) {
        _estado.update { it.copy(nombre = valor, errores = it.errores.copy(nombre = null)) }
    }

    fun actualizaCorreo(valor: String) {
        _estado.update { it.copy(correo = valor, errores = it.errores.copy(correo = null)) }
    }

    fun actualizaTelefono(valor: String) {
        // Filtrar solo números
        val soloNumeros = valor.filter { it.isDigit() }
        _estado.update { it.copy(telefono = soloNumeros, errores = it.errores.copy(telefono = null)) }
    }

    fun actualizaClave(valor: String) {
        _estado.update { it.copy(clave = valor, errores = it.errores.copy(clave = null)) }
    }

    fun actualizaConfirmarClave(valor: String) {
        _estado.update { it.copy(confirmarClave = valor, errores = it.errores.copy(confirmarClave = null)) }
    }

    fun actualizaDireccion(valor: String) {
        _estado.update { it.copy(direccion = valor, errores = it.errores.copy(direccion = null)) }
    }

    fun actualizaAceptaTerminos(valor: Boolean) {
        _estado.update { it.copy(aceptaTerminos = valor, errores = it.errores.copy(aceptaTerminos = null)) }
    }

    // Validación del formulario
    fun validarFormulario(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var errores = UsuarioErrores()

        if (estadoActual.nombre.isBlank()) {
            errores = errores.copy(nombre = "El nombre es obligatorio")
            valido = false
        }
        if (estadoActual.correo.isBlank() || !estadoActual.correo.contains("@")) {
            errores = errores.copy(correo = "Correo inválido")
            valido = false
        }
        // Teléfono es opcional en el registro, solo validamos si no está vacío
        if (estadoActual.telefono.isNotBlank() && estadoActual.telefono.length < 8) {
            errores = errores.copy(telefono = "El teléfono debe tener al menos 8 dígitos")
            valido = false
        }
        if (estadoActual.clave.length < 6) {
            errores = errores.copy(clave = "La clave debe tener al menos 6 caracteres")
            valido = false
        }
        if (estadoActual.confirmarClave.isBlank()) {
            errores = errores.copy(confirmarClave = "Debe confirmar la contraseña")
            valido = false
        } else if (estadoActual.clave != estadoActual.confirmarClave) {
            errores = errores.copy(confirmarClave = "Las contraseñas no coinciden")
            valido = false
        }
        if (!estadoActual.aceptaTerminos) {
            errores = errores.copy(aceptaTerminos = "Debes aceptar los términos")
            valido = false
        }

        // Actualizamos el estado con los errores
        _estado.update { it.copy(errores = errores) }

        return valido
    }

    // Registrar usuario en la base de datos
    fun registrarUsuario(onSuccess: () -> Unit) {
        if (!validarFormulario()) {
            return
        }

        val estadoActual = _estado.value
        viewModelScope.launch {
            val resultado = repository.register(
                nombre = estadoActual.nombre,
                correo = estadoActual.correo,
                telefono = estadoActual.telefono,
                clave = estadoActual.clave,
                direccion = estadoActual.direccion
            )

            resultado.onSuccess { userId ->
                _registroResultado.value = "Usuario registrado exitosamente"
                // Obtener el usuario recién creado y guardar sesión
                val newUser = repository.getUserById(userId)
                newUser?.let {
                    sessionManager.saveSession(it)
                    _currentUser.value = it
                }
                // Limpiar el formulario
                _estado.update { UsuarioUiState() }
                onSuccess()
            }.onFailure { error ->
                _registroResultado.value = error.message ?: "Error al registrar"
                _estado.update {
                    it.copy(
                        errores = it.errores.copy(
                            correo = if (error.message?.contains("ya está registrado") == true)
                                error.message else null
                        )
                    )
                }
            }
        }
    }

    fun limpiarMensajeRegistro() {
        _registroResultado.value = null
    }

    // Cargar el perfil del usuario logueado
    fun cargarPerfil() {
        viewModelScope.launch {
            val user = sessionManager.getCurrentUser()
            if (user != null) {
                _currentUser.value = user
                _estado.update {
                    it.copy(
                        nombre = user.nombre,
                        correo = user.correo,
                        telefono = user.telefono,
                        direccion = user.direccion
                    )
                }
                // Cargar la foto de perfil guardada
                _profileImageUri.value = sessionManager.getProfileImageUri()
            }
        }
    }

    // Guardar sesión después del login (llamar desde LoginScreen)
    fun saveSession(user: UserEntity) {
        sessionManager.saveSession(user)
        _currentUser.value = user
        cargarPerfil()
    }

    // Activar modo edición
    fun activarEdicion() {
        _isEditMode.value = true
    }

    // Cancelar edición (restaurar valores originales)
    fun cancelarEdicion() {
        _isEditMode.value = false
        cargarPerfil() // Recargar los valores originales
    }

    // Guardar cambios del perfil
    fun guardarCambiosPerfil(onSuccess: () -> Unit) {
        if (!validarFormularioPerfil()) {
            return
        }

        val currentUserId = _currentUser.value?.id
        val currentPassword = _currentUser.value?.clave

        if (currentUserId == null || currentPassword == null) {
            _updateResultado.value = "Error: No hay sesión activa"
            return
        }

        val estadoActual = _estado.value
        val updatedUser = UserEntity(
            id = currentUserId,
            nombre = estadoActual.nombre,
            correo = estadoActual.correo,
            telefono = estadoActual.telefono,
            clave = currentPassword, // Mantener la misma contraseña
            direccion = estadoActual.direccion
        )

        viewModelScope.launch {
            val resultado = repository.updateUser(updatedUser)
            resultado.onSuccess {
                sessionManager.updateSession(updatedUser)
                _currentUser.value = updatedUser
                _updateResultado.value = "Perfil actualizado exitosamente"
                _isEditMode.value = false
                onSuccess()
            }.onFailure { error ->
                _updateResultado.value = error.message ?: "Error al actualizar perfil"
            }
        }
    }

    // Validación del formulario de perfil (similar al registro pero sin clave ni términos)
    private fun validarFormularioPerfil(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var errores = UsuarioErrores()

        if (estadoActual.nombre.isBlank()) {
            errores = errores.copy(nombre = "El nombre es obligatorio")
            valido = false
        }
        if (estadoActual.correo.isBlank() || !estadoActual.correo.contains("@")) {
            errores = errores.copy(correo = "Correo inválido")
            valido = false
        }
        // Teléfono es opcional, solo validamos si no está vacío
        if (estadoActual.telefono.isNotBlank() && estadoActual.telefono.length < 8) {
            errores = errores.copy(telefono = "El teléfono debe tener al menos 8 dígitos")
            valido = false
        }
        if (estadoActual.direccion.isBlank()) {
            errores = errores.copy(direccion = "La dirección es obligatoria")
            valido = false
        }

        _estado.update { it.copy(errores = errores) }
        return valido
    }

    // Cerrar sesión
    fun cerrarSesion() {
        sessionManager.clearSession()
        _currentUser.value = null
        _estado.update { UsuarioUiState() }
        _isEditMode.value = false
        _profileImageUri.value = null
    }

    // Verificar si hay sesión activa
    fun isLoggedIn(): Boolean {
        return sessionManager.isLoggedIn()
    }

    fun limpiarMensajeActualizacion() {
        _updateResultado.value = null
    }

    // Guardar la URI de la foto de perfil
    fun guardarFotoPerfil(uri: String?) {
        _profileImageUri.value = uri
        sessionManager.saveProfileImageUri(uri)
    }

    // Eliminar la foto de perfil
    fun eliminarFotoPerfil() {
        _profileImageUri.value = null
        sessionManager.clearProfileImage()
    }
}

package com.example.appajicolorgrupo4.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.appajicolorgrupo4.data.local.database.AppDatabase
import com.example.appajicolorgrupo4.data.repository.UserRepository
import com.example.appajicolorgrupo4.data.session.SessionManager
import com.example.appajicolorgrupo4.data.local.user.UserEntity
import com.example.appajicolorgrupo4.ui.state.UsuarioUiState
import com.example.appajicolorgrupo4.ui.state.UsuarioErrores
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch

class UsuarioViewModel(application: Application) : AndroidViewModel(application) {

    // Repositorio y SessionManager
    private val repository: UserRepository
    private val sessionManager: SessionManager

    init {
        val database = AppDatabase.getInstance(application)
        repository = UserRepository(database.userDao())
        sessionManager = SessionManager(application)
    }

    // Estado privado mutable
    private val _estado = MutableStateFlow(UsuarioUiState())
    // Estado público inmutable para la UI
    val estado: StateFlow<UsuarioUiState> = _estado

    // Estado para mensajes de éxito/error al registrar
    private val _registroResultado = MutableStateFlow<String?>(null)
    val registroResultado: StateFlow<String?> = _registroResultado

    // Estado para el usuario actual
    private val _currentUser = MutableStateFlow<UserEntity?>(null)
    val currentUser: StateFlow<UserEntity?> = _currentUser

    // Estado para el modo de edición
    private val _isEditMode = MutableStateFlow(false)
    val isEditMode: StateFlow<Boolean> = _isEditMode

    // Estado para mensajes de actualización
    private val _updateResultado = MutableStateFlow<String?>(null)
    val updateResultado: StateFlow<String?> = _updateResultado

    // Estado para la URI de la foto de perfil
    private val _profileImageUri = MutableStateFlow<String?>(null)
    val profileImageUri: StateFlow<String?> = _profileImageUri

    // Funciones de actualización de campos
    fun actualizaNombre(valor: String) {
        _estado.update { it.copy(nombre = valor, errores = it.errores.copy(nombre = null)) }
    }

    fun actualizaCorreo(valor: String) {
        _estado.update { it.copy(correo = valor, errores = it.errores.copy(correo = null)) }
    }

    fun actualizaTelefono(valor: String) {
        // Filtrar solo números
        val soloNumeros = valor.filter { it.isDigit() }
        _estado.update { it.copy(telefono = soloNumeros, errores = it.errores.copy(telefono = null)) }
    }

    fun actualizaClave(valor: String) {
        _estado.update { it.copy(clave = valor, errores = it.errores.copy(clave = null)) }
    }

    fun actualizaConfirmarClave(valor: String) {
        _estado.update { it.copy(confirmarClave = valor, errores = it.errores.copy(confirmarClave = null)) }
    }

    fun actualizaDireccion(valor: String) {
        _estado.update { it.copy(direccion = valor, errores = it.errores.copy(direccion = null)) }
    }

    fun actualizaAceptaTerminos(valor: Boolean) {
        _estado.update { it.copy(aceptaTerminos = valor, errores = it.errores.copy(aceptaTerminos = null)) }
    }

    // Validación del formulario
    fun validarFormulario(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var errores = UsuarioErrores()

        if (estadoActual.nombre.isBlank()) {
            errores = errores.copy(nombre = "El nombre es obligatorio")
            valido = false
        }
        if (estadoActual.correo.isBlank() || !estadoActual.correo.contains("@")) {
            errores = errores.copy(correo = "Correo inválido")
            valido = false
        }
        // Teléfono ya no es opcional - es obligatorio
        if (estadoActual.telefono.isBlank()) {
            errores = errores.copy(telefono = "El teléfono es obligatorio")
            valido = false
        } else if (estadoActual.telefono.length < 8) {
            errores = errores.copy(telefono = "El teléfono debe tener al menos 8 dígitos")
            valido = false
        }
        if (estadoActual.clave.length < 6) {
            errores = errores.copy(clave = "La clave debe tener al menos 6 caracteres")
            valido = false
        }
        if (estadoActual.confirmarClave.isBlank()) {
            errores = errores.copy(confirmarClave = "Debe confirmar la contraseña")
            valido = false
        } else if (estadoActual.clave != estadoActual.confirmarClave) {
            errores = errores.copy(confirmarClave = "Las contraseñas no coinciden")
            valido = false
        }
        if (!estadoActual.aceptaTerminos) {
            errores = errores.copy(aceptaTerminos = "Debes aceptar los términos")
            valido = false
        }

        // Actualizamos el estado con los errores
        _estado.update { it.copy(errores = errores) }

        return valido
    }

    // Registrar usuario en la base de datos
    fun registrarUsuario(onSuccess: () -> Unit) {
        if (!validarFormulario()) {
            return
        }

        val estadoActual = _estado.value
        viewModelScope.launch {
            val resultado = repository.register(
                nombre = estadoActual.nombre,
                correo = estadoActual.correo,
                telefono = estadoActual.telefono,
                clave = estadoActual.clave,
                direccion = estadoActual.direccion
            )

            resultado.onSuccess { userId ->
                _registroResultado.value = "Usuario registrado exitosamente"
                // Obtener el usuario recién creado y guardar sesión
                val newUser = repository.getUserById(userId)
                newUser?.let {
                    sessionManager.saveSession(it)
                    _currentUser.value = it
                }
                // Limpiar el formulario
                _estado.update { UsuarioUiState() }
                onSuccess()
            }.onFailure { error ->
                _registroResultado.value = error.message ?: "Error al registrar"
                _estado.update {
                    it.copy(
                        errores = it.errores.copy(
                            correo = if (error.message?.contains("ya está registrado") == true)
                                error.message else null
                        )
                    )
                }
            }
        }
    }

    fun limpiarMensajeRegistro() {
        _registroResultado.value = null
    }

    // Cargar el perfil del usuario logueado
    fun cargarPerfil() {
        viewModelScope.launch {
            val user = sessionManager.getCurrentUser()
            if (user != null) {
                _currentUser.value = user
                _estado.update {
                    it.copy(
                        nombre = user.nombre,
                        correo = user.correo,
                        telefono = user.telefono,
                        direccion = user.direccion
                    )
                }
                // Cargar la foto de perfil guardada
                _profileImageUri.value = sessionManager.getProfileImageUri()
            }
        }
    }

    // Guardar sesión después del login (llamar desde LoginScreen)
    fun saveSession(user: UserEntity) {
        sessionManager.saveSession(user)
        _currentUser.value = user
        cargarPerfil()
    }

    // Activar modo edición
    fun activarEdicion() {
        _isEditMode.value = true
    }

    // Cancelar edición (restaurar valores originales)
    fun cancelarEdicion() {
        _isEditMode.value = false
        cargarPerfil() // Recargar los valores originales
    }

    // Guardar cambios del perfil
    fun guardarCambiosPerfil(onSuccess: () -> Unit) {
        if (!validarFormularioPerfil()) {
            return
        }

        val currentUserId = _currentUser.value?.id
        val currentPassword = _currentUser.value?.clave

        if (currentUserId == null || currentPassword == null) {
            _updateResultado.value = "Error: No hay sesión activa"
            return
        }

        val estadoActual = _estado.value
        val updatedUser = UserEntity(
            id = currentUserId,
            nombre = estadoActual.nombre,
            correo = estadoActual.correo,
            telefono = estadoActual.telefono,
            clave = currentPassword, // Mantener la misma contraseña
            direccion = estadoActual.direccion
        )

        viewModelScope.launch {
            val resultado = repository.updateUser(updatedUser)
            resultado.onSuccess {
                sessionManager.updateSession(updatedUser)
                _currentUser.value = updatedUser
                _updateResultado.value = "Perfil actualizado exitosamente"
                _isEditMode.value = false
                onSuccess()
            }.onFailure { error ->
                _updateResultado.value = error.message ?: "Error al actualizar perfil"
            }
        }
    }

    // Validación del formulario de perfil (similar al registro pero sin clave ni términos)
    private fun validarFormularioPerfil(): Boolean {
        val estadoActual = _estado.value
        var valido = true
        var errores = UsuarioErrores()

        if (estadoActual.nombre.isBlank()) {
            errores = errores.copy(nombre = "El nombre es obligatorio")
            valido = false
        }
        if (estadoActual.correo.isBlank() || !estadoActual.correo.contains("@")) {
            errores = errores.copy(correo = "Correo inválido")
            valido = false
        }
        // Teléfono es obligatorio
        if (estadoActual.telefono.isBlank()) {
            errores = errores.copy(telefono = "El teléfono es obligatorio")
            valido = false
        } else if (estadoActual.telefono.length < 8) {
            errores = errores.copy(telefono = "El teléfono debe tener al menos 8 dígitos")
            valido = false
        }
        if (estadoActual.direccion.isBlank()) {
            errores = errores.copy(direccion = "La dirección es obligatoria")
            valido = false
        }

        _estado.update { it.copy(errores = errores) }
        return valido
    }

    // Cerrar sesión
    fun cerrarSesion() {
        sessionManager.clearSession()
        _currentUser.value = null
        _estado.update { UsuarioUiState() }
        _isEditMode.value = false
        _profileImageUri.value = null
    }

    // Verificar si hay sesión activa
    fun isLoggedIn(): Boolean {
        return sessionManager.isLoggedIn()
    }

    fun limpiarMensajeActualizacion() {
        _updateResultado.value = null
    }

    // Guardar la URI de la foto de perfil
    fun guardarFotoPerfil(uri: String?) {
        _profileImageUri.value = uri
        sessionManager.saveProfileImageUri(uri)
    }

    // Eliminar la foto de perfil
    fun eliminarFotoPerfil() {
        _profileImageUri.value = null
        sessionManager.clearProfileImage()
    }
}

package com.example.apppolera_ecommerce_grupo4.ui.screens

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.apppolera_ecommerce_grupo4.viewmodel.UsuarioViewModel

@Composable
fun RegistroScreen(
    navController: NavController,

) {
    val estado by viewModel.estado.collectAsState()

    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        verticalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        // Campo Nombre
        OutlinedTextField(
            value = estado.nombre,
            onValueChange = viewModel::actualizaNombre,
            label = { Text("Nombre") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.nombre != null,
            supportingText = { estado.errores.nombre?.let { Text(it, color = Color.Red) } }
        )

        // Campo Correo
        OutlinedTextField(
            value = estado.correo,
            onValueChange = viewModel::actualizaCorreo,
            label = { Text("Correo electrónico") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.correo != null,
            supportingText = { estado.errores.correo?.let { Text(it, color = Color.Red) } }
        )

        // Campo Clave
        OutlinedTextField(
            value = estado.clave,
            onValueChange = viewModel::actualizaClave,
            label = { Text("Clave") },
            modifier = Modifier.fillMaxWidth(),
            visualTransformation = PasswordVisualTransformation(),
            isError = estado.errores.clave != null,
            supportingText = { estado.errores.clave?.let { Text(it, color = Color.Red) } }
        )

        // Campo Dirección
        OutlinedTextField(
            value = estado.direccion,
            onValueChange = viewModel::actualizaDireccion,
            label = { Text("Dirección") },
            modifier = Modifier.fillMaxWidth(),
            isError = estado.errores.direccion != null,
            supportingText = { estado.errores.direccion?.let { Text(it, color = Color.Red) } }
        )

        // Checkbox términos
        Row(verticalAlignment = androidx.compose.ui.Alignment.CenterVertically) {
            Checkbox(
                checked = estado.aceptaTerminos,
                onCheckedChange = viewModel::actualizaAceptaTerminos
            )
            Text("Acepto los términos y condiciones")
        }
        if (estado.errores.aceptaTerminos != null) {
            Text(estado.errores.aceptaTerminos!!, color = Color.Red)
        }

        // Botón registrar
        Button(
            onClick = {
                if (viewModel.validarFormulario()) {
                    navController.navigate(Routes.RESUMEN)
                }
            },
            modifier = Modifier.fillMaxWidth()
        ) {
            Text("Registrarse")
        }
    }
}

